AND A.status = #{status}

SELECT A.id, A.adspot_name, A.adspot_type, C.name AS adspot_type_name, B.platform_type, A.media_id, B.media_name, B.bundle_ids, A.status, A.timeout, A.device_daily_req_limit, A.device_daily_imp_limit, A.device_req_interval, IF(TRIM(COALESCE(A.special_settings, '')) = '', '{}', A.special_settings) AS special_settings FROM adspot A LEFT OUTER JOIN media B ON A.media_id = B.id LEFT OUTER JOIN ( SELECT value, name FROM system_code WHERE code_type_id = 2 AND status = 1 ) C ON A.adspot_type = C.value WHERE A.mark_delete = 0 AND B.mark_delete = 0
AND A.media_id in
#{item}
AND A.adspot_type in
#{item}
AND (A.id = #{searchText} OR A.id rlike #{searchText} OR A.adspot_name rlike #{searchText})
AND A.status = #{status}
ORDER BY A.status desc, A.id desc
limit #{limit}
offset #{offset}
