mapper.select:
SELECT count(id) FROM media WHERE mark_delete = 0
AND (id = #{searchText} OR id rlike #{searchText} OR media_name rlike #{searchText})
AND platform_type = #{platform}
AND status = #{status}

SELECT A.id, A.platform_type, COALESCE(C.name, "") AS platform_type_name, A.media_name, A.bundle_ids, COALESCE(B.adspot_cnt, 0) AS adspot_cnt, A.status FROM ( SELECT * FROM media WHERE mark_delete = 0
AND (id = #{searchText} OR id rlike #{searchText} OR media_name rlike #{searchText})
AND platform_type = #{platform}
AND status = #{status}
ORDER BY status desc, id desc
limit #{limit}
offset #{offset}
) A LEFT OUTER JOIN ( SELECT media_id, count(id) AS adspot_cnt FROM adspot WHERE mark_delete = 0 GROUP BY media_id ) B ON A.id = B.media_id LEFT OUTER JOIN ( SELECT value, name FROM system_code WHERE code_type_id = 1 AND status = 1 ) C ON A.platform_type = C.value

mapper.update:
UPDATE media
SET media_name = #{media.mediaName}, bundle_ids = #{media.bundleName}, status = #{media.status}
WHERE id = #{mediaId}
