mapper.select:
SELECT A.id AS sdk_traffic_id, B.id AS sdk_group_percentage_id, B.tag, B.percentage, C.id AS sdk_group_targeting_id, C.name, C.priority, C.app_version, C.sdk_version, A.supplier_ids FROM sdk_group A JOIN sdk_group_percentage B ON A.sdk_group_percentage_id = B.id JOIN sdk_group_targeting C ON A.sdk_group_targeting_id = C.id WHERE A.adspot_id = #{adspotId}
AND A.sdk_group_percentage_id = #{percentageId}
ORDER BY B.id, C.priority ASC

SELECT id, supplier_ids
FROM sdk_group
WHERE adspot_id = #{adspotId} AND id = #{sdkTrafficId}

mapper.update:
UPDATE sdk_group
SET supplier_ids = #{supplierIds}
WHERE id = #{sdkTrafficId}

mapper.select:
SELECT id, supplier_ids
FROM sdk_group
WHERE adspot_id = #{adspotId} AND supplier_ids rlike #{sdkChannelId}
